import React, { useState } from "react";
import { Button, Form } from "react-bootstrap";
import "./login.css";
import Navbar from "./Navbar";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loggedIn, setLoggedIn] = useState(false); // State to track login status

  const validateForm = () => {
    return email.length > 0 && password.length > 0;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!validateForm()) {
      setError("Please fill in all fields.");
      return;
    }

    // Perform login logic here, e.g., make API calls, check credentials
    if (email === "rayen@gmail.com") {
      setLoggedIn(true); // Set loggedIn state to true if email matches
    } else {
      setError("Invalid email or password");
    }
  };

  return (
    <div className="App">
      {!loggedIn ? ( // Render login form if not logged in
        <Form onSubmit={handleSubmit}>
          <Form.Group size="lg" controlId="email">
            <Form.Label>Email</Form.Label>
            <Form.Control
              autoFocus
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </Form.Group>
          <Form.Group size="lg" controlId="password">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </Form.Group>
          {error && <p style={{ color: "red" }}>{error}</p>}
          <Button size="lg" type="submit" disabled={!validateForm()}>
            Login
          </Button>
        </Form>
      ) : (
        <>
        <Navbar /> 
        </>// Render Navbar component if logged in
      )}
    </div>
  );
}

export default Login;
